pragma solidity 0.8.20;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "./INTPair.sol";

contract INTFactory is
    Initializable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CREATOR_ROLE = keccak256("CREATOR_ROLE");

    mapping(address => mapping(address => address)) private _pair;

    address[] public pairs;

    address public router;

    address public taxVault;
    uint256 public buyTax;
    uint256 public sellTax;
    uint256 public treasuryFeeRatio; // 1 => 1%

    event TreasuryFeeRatioUpdated(uint256 oldRatio, uint256 newRatio);

    event PairCreated(
        address indexed tokenA,
        address indexed tokenB,
        address pair,
        uint256 totalPairs
    );

    error TreasuryFeeRatioTooHigh();
    error SameTreasuryFeeRatio();

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address taxVault_,
        uint256 buyTax_,
        uint256 sellTax_,
        uint256 treasuryFeeRatio_
    ) external initializer {
        __AccessControl_init();
        __ReentrancyGuard_init();
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        if (treasuryFeeRatio_ > 100) revert TreasuryFeeRatioTooHigh();

        taxVault = taxVault_;
        buyTax = buyTax_;
        sellTax = sellTax_;
        treasuryFeeRatio = treasuryFeeRatio_;
    }

    function _createPair(
        address tokenA,
        address tokenB
    ) internal returns (address) {
        require(tokenA != address(0), "Zero addresses are not allowed.");
        require(tokenB != address(0), "Zero addresses are not allowed.");

        INTPair pair_ = new INTPair(address(this), tokenA, tokenB);

        _pair[tokenA][tokenB] = address(pair_);
        _pair[tokenB][tokenA] = address(pair_);

        pairs.push(address(pair_));

        uint256 totalPairs = pairs.length;

        emit PairCreated(tokenA, tokenB, address(pair_), totalPairs);

        return address(pair_);
    }

    function createPair(
        address tokenA,
        address tokenB
    ) external onlyRole(CREATOR_ROLE) nonReentrant returns (address) {
        address pair = _createPair(tokenA, tokenB);

        return pair;
    }

    function getPair(
        address tokenA,
        address tokenB
    ) public view returns (address) {
        return _pair[tokenA][tokenB];
    }

    function allPairsLength() public view returns (uint256) {
        return pairs.length;
    }

    function setTaxParams(
        address newVault_,
        uint256 buyTax_,
        uint256 sellTax_
    ) public onlyRole(ADMIN_ROLE) {
        require(newVault_ != address(0), "Zero addresses are not allowed.");

        taxVault = newVault_;
        buyTax = buyTax_;
        sellTax = sellTax_;
    }

    function setRouter(address router_) public onlyRole(ADMIN_ROLE) {
        router = router_;
    }

    /**
     * @notice Only accounts have the admin role can call this function
     * @dev Set the treasury fee ratio
     * @param newRatio The new treasury fee ratio
     */
    function setTreasuryFeeRatio(
        uint256 newRatio
    ) external onlyRole(ADMIN_ROLE) {
        if (newRatio > 100) revert TreasuryFeeRatioTooHigh();

        uint256 oldRatio = treasuryFeeRatio;
        if (newRatio == oldRatio) revert SameTreasuryFeeRatio();

        treasuryFeeRatio = newRatio;

        emit TreasuryFeeRatioUpdated(oldRatio, newRatio);
    }
}
","keccak256":"0x4eb34904b4dc6f86e8c3986999b6b9ee7c3501f07fd7e7c0d8b9294b4ce4b6ac","license":"MIT"},"src/INTPair.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./IINTPair.sol";
import "./INTFactory.sol";

contract INTPair is IINTPair, ReentrancyGuard {
    using SafeERC20 for IERC20;

    address public immutable factory;
    address public immutable tokenA; // meme token
    address public immutable tokenB;

    struct Pool {
        uint256 reserve0;
        uint256 reserve1;
        uint256 k;
        uint256 lastUpdated;
    }

    Pool private _pool;

    constructor(address factory_, address token0, address token1) {
        require(factory_ != address(0), "Zero addresses are not allowed.");
        require(token0 != address(0), "Zero addresses are not allowed.");
        require(token1 != address(0), "Zero addresses are not allowed.");

        factory = factory_;
        tokenA = token0;
        tokenB = token1;
    }

    modifier onlyRouter() {
        require(
            INTFactory(factory).router() == msg.sender,
            "Only router can call this function"
        );
        _;
    }

    event Mint(uint256 reserve0, uint256 reserve1);

    event Swap(
        uint256 amount0In,
        uint256 amount0Out,
        uint256 amount1In,
        uint256 amount1Out
    );

    function mint(
        uint256 reserve0,
        uint256 reserve1
    ) public onlyRouter returns (bool) {
        require(_pool.lastUpdated == 0, "Already minted");

        _pool = Pool({
            reserve0: reserve0,
            reserve1: reserve1,
            k: reserve0 * reserve1,
            lastUpdated: block.timestamp
        });

        emit Mint(reserve0, reserve1);

        return true;
    }

    function swap(
        uint256 amount0In,
        uint256 amount0Out,
        uint256 amount1In,
        uint256 amount1Out
    ) public onlyRouter returns (bool) {
        uint256 _reserve0 = (_pool.reserve0 + amount0In) - amount0Out;
        uint256 _reserve1 = (_pool.reserve1 + amount1In) - amount1Out;

        _pool = Pool({
            reserve0: _reserve0,
            reserve1: _reserve1,
            k: _pool.k,
            lastUpdated: block.timestamp
        });

        emit Swap(amount0In, amount0Out, amount1In, amount1Out);

        return true;
    }

    function approval(
        address _user,
        address _token,
        uint256 amount
    ) public onlyRouter returns (bool) {
        require(_user != address(0), "Zero addresses are not allowed.");
        require(_token != address(0), "Zero addresses are not allowed.");

        IERC20 token = IERC20(_token);

        token.forceApprove(_user, amount);

        return true;
    }

    function transferAsset(
        address recipient,
        uint256 amount
    ) public onlyRouter {
        require(recipient != address(0), "Zero addresses are not allowed.");

        IERC20(tokenB).safeTransfer(recipient, amount);
    }

    function transferTo(address recipient, uint256 amount) public onlyRouter {
        require(recipient != address(0), "Zero addresses are not allowed.");

        IERC20(tokenA).safeTransfer(recipient, amount);
    }

    function getReserves() public view returns (uint256, uint256) {
        return (_pool.reserve0, _pool.reserve1);
    }

    function kLast() public view returns (uint256) {
        return _pool.k;
    }

    function priceALast() public view returns (uint256) {
        return _pool.reserve1 / _pool.reserve0;
    }

    function priceBLast() public view returns (uint256) {
        return _pool.reserve0 / _pool.reserve1;
    }

    function balance() public view returns (uint256) {
        return IERC20(tokenA).balanceOf(address(this));
    }

    function assetBalance() public view returns (uint256) {
        return IERC20(tokenB).balanceOf(address(this));
    }
}
","keccak256":"0xe8981579dd0538b8d8c73c52e8c73f63899b6bec8a53495996d51bc3eaedce5e","license":"MIT"},"src/INTRouterLibrary.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./INTFactory.sol";
import "./IINTPair.sol";
import "./interface/IBonding.sol";
import "./interface/ILock.sol";

library INTRouterLibrary {
    using SafeERC20 for IERC20;

    error FactoryIsZeroAddress();
    error AssetTokenIsZeroAddress();

    event Buy(
        address indexed account,
        address indexed token,
        uint256 tokenAmount,
        uint256 assetAmount,
        uint256 assetFee,
        uint256 tokenReserve,
        uint256 assetReserve,
        uint256 timestamp
    );
    event Sell(
        address indexed account,
        address indexed token,
        uint256 tokenAmount,
        uint256 assetAmount,
        uint256 assetFee,
        uint256 tokenReserve,
        uint256 assetReserve,
        uint256 timestamp
    );

    function _getReserves(
        INTFactory factory,
        address assetToken,
        address token
    ) internal view returns (uint256 reserveA, uint256 reserveB, uint256 k) {
        address pairAddress = factory.getPair(token, assetToken);
        IINTPair pair = IINTPair(pairAddress);
        (reserveA, reserveB) = pair.getReserves();
        k = pair.kLast();
    }

    function quoteBuy(
        INTFactory factory,
        address assetToken,
        address token,
        uint256 amountIn
    ) public view returns (uint256 amountOut, uint256 txFee) {
        require(token != address(0), "Zero addresses are not allowed.");

        (uint256 reserveA, uint256 reserveB, uint256 k) = _getReserves(
            factory,
            assetToken,
            token
        );

        uint256 fee = factory.buyTax();
        txFee = (fee * amountIn) / 100;
        amountIn = amountIn - txFee;

        uint256 newReserveB = reserveB + amountIn;
        uint256 newReserveA = k / newReserveB;
        amountOut = reserveA - newReserveA;
    }

    function quoteSell(
        INTFactory factory,
        address assetToken,
        address token,
        uint256 amountIn
    ) public view returns (uint256 amountOut, uint256 txFee) {
        require(token != address(0), "Zero addresses are not allowed.");

        (uint256 reserveA, uint256 reserveB, uint256 k) = _getReserves(
            factory,
            assetToken,
            token
        );

        uint256 newReserveA = reserveA + amountIn;
        uint256 newReserveB = k / newReserveA;
        amountOut = reserveB - newReserveB;

        uint256 fee = factory.sellTax();
        txFee = (fee * amountOut) / 100;
        amountOut = amountOut - txFee;
    }

    function addInitialLiquidity(
        INTFactory factory,
        address assetToken,
        address token_,
        uint256 amountToken_,
        uint256 amountAsset_
    ) public returns (uint256, uint256) {
        require(token_ != address(0), "Zero addresses are not allowed.");

        address pairAddress = factory.getPair(token_, assetToken);

        IINTPair pair = IINTPair(pairAddress);

        IERC20 token = IERC20(token_);

        token.safeTransfer(pairAddress, amountToken_);

        pair.mint(amountToken_, amountAsset_);

        return (amountToken_, amountAsset_);
    }

    /**
     * @dev Distribute fee to treasury and lock contract based on the `treasuryFeeRatio`
     * @param from The address that buying the token, address(0) if selling
     * @param pair The pair contract When selling
     * @param fromToken The token that is being traded
     * @param amount The amount of token that is being traded
     */
    function collectFee(
        INTFactory factory,
        address assetToken,
        address from,
        address pair,
        address fromToken,
        uint256 amount
    ) internal {
        uint256 treasuryFeeRatio = factory.treasuryFeeRatio();
        uint256 treasuryFee = (amount * treasuryFeeRatio) / 100;
        // TODO: Treasury?
        address treasuryFeeTo = factory.taxVault();

        uint256 lockFee = amount - treasuryFee;
        address lockFeeTo = IBonding(address(this)).getTokenLocker(fromToken);

        if (from == address(0)) {
            // Selling
            // Transfer treasury fee to treasury directly
            IINTPair(pair).transferAsset(treasuryFeeTo, treasuryFee);

            // Transfer lock fee to this contract
            IINTPair(pair).transferAsset(address(this), lockFee);
        } else {
            // Buying
            // Transfer treasury fee to treasury directly
            IERC20(assetToken).safeTransferFrom(
                from,
                treasuryFeeTo,
                treasuryFee
            );

            // Transfer lock fee to this contract
            IERC20(assetToken).safeTransferFrom(from, address(this), lockFee);
        }

        // Approve to lock contract to deposit lock fee
        IERC20(assetToken).forceApprove(lockFeeTo, lockFee);
        // Deposit lock fee to lock contract
        ILock(lockFeeTo).depositFee(lockFee);
    }

    function sell(
        INTFactory factory,
        address assetToken,
        uint256 amountIn,
        address tokenAddress,
        address to
    ) public returns (uint256, uint256) {
        require(tokenAddress != address(0), "Zero addresses are not allowed.");
        require(to != address(0), "Zero addresses are not allowed.");

        address pairAddress = factory.getPair(tokenAddress, assetToken);

        IINTPair pair = IINTPair(pairAddress);

        IERC20 token = IERC20(tokenAddress);

        (uint256 amountOut, uint256 txFee) = quoteSell(
            factory,
            assetToken,
            tokenAddress,
            amountIn
        );

        token.safeTransferFrom(to, pairAddress, amountIn);

        pair.transferAsset(to, amountOut);
        collectFee(
            factory,
            assetToken,
            address(0),
            address(pair),
            tokenAddress,
            txFee
        );

        pair.swap(amountIn, 0, 0, amountOut);

        (uint256 reserveA, uint256 reserveB) = IINTPair(pair).getReserves();

        emit Sell(
            to,
            tokenAddress,
            amountIn,
            amountOut,
            txFee,
            reserveA,
            reserveB,
            block.timestamp
        );

        return (amountIn, amountOut);
    }

    function buy(
        INTFactory factory,
        address assetToken,
        uint256 amountIn,
        address tokenAddress,
        address to
    ) public returns (uint256, uint256) {
        require(tokenAddress != address(0), "Zero addresses are not allowed.");
        require(to != address(0), "Zero addresses are not allowed.");
        require(amountIn > 0, "amountIn must be greater than 0");

        address pair = factory.getPair(tokenAddress, assetToken);

        (uint256 amountOut, uint256 txFee) = quoteBuy(
            factory,
            assetToken,
            tokenAddress,
            amountIn
        );
        uint256 amount = amountIn - txFee;

        IERC20(assetToken).safeTransferFrom(to, pair, amount);

        collectFee(factory, assetToken, to, address(0), tokenAddress, txFee);

        IINTPair(pair).transferTo(to, amountOut);

        IINTPair(pair).swap(0, amountOut, amount, 0);

        (uint256 reserveA, uint256 reserveB) = IINTPair(pair).getReserves();

        emit Buy(
            to,
            tokenAddress,
            amountOut,
            amount,
            txFee,
            reserveA,
            reserveB,
            block.timestamp
        );

        return (amountIn, amountOut);
    }

    function graduate(
        INTFactory factory,
        address assetToken,
        address tokenAddress
    ) public {
        require(tokenAddress != address(0), "Zero addresses are not allowed.");
        address pair = factory.getPair(tokenAddress, assetToken);
        uint256 assetBalance = IINTPair(pair).assetBalance();
        uint256 balance = IINTPair(pair).balance();
        INTPair(pair).transferAsset(address(this), assetBalance);
        INTPair(pair).transferTo(address(this), balance);
    }
}
